def draw_path(num_tuple, nsteps):
    """
    Use find_nsteps to find the path and use the path to draw the plot with 4 directions
    input:
    num_tuple: a tuple of two integers, the numerator and denominator of a fraction
    nsteps: the number of steps to take
    output:
    the plot of the path
    """
    num_list = find_nsteps(num_tuple, 4, nsteps)[-1]
    dict={}
    t=1
    location=(0,0)
    for i in num_list:
        dict[t]=location
        if i == "0":
            location=(location[0]+1,location[1])
        elif i == "1":
            location=(location[0],location[1]+1)
        elif i == "2":
            location=(location[0]-1,location[1])
        elif i == "3":
            location=(location[0],location[1]-1)
        # dict[t]=location
        t+=1
    x = [v[0] for v in dict.values()]
    y = [v[1] for v in dict.values()]
    i=list(range(1, len(x)+1))
    plt.scatter(x, y, s=10, c=i, cmap=cm.jet)
    plt.show()
# # Part 4: Return (m,n)
def from_prefix_period2mn(prefix, period,base):
    '''
    This function is used to convert the prefix and period to the form of (m,n)
    The math infromation is in the memo document
    warning: this function can only be used when the base less than 36
    input:
    prefix: the list of prefix 
    period: the list of period
    b: the base
    output:
    tuple of the form (numerator, denominator)
    '''
    prefix= ''.join(map(str,prefix))
    period= ''.join(map(str,period))
    if period=="":
        period="0"
    if prefix=="":
        prefix=period
    prefix_length=len(prefix)
    period_length=len(period)
    m=int(prefix,base)*base**period_length+int(period,base)-int(prefix,base)
    n=base**(prefix_length)*(base**period_length-1)

    gcd = math.gcd(m,n)
    return m//gcd, n//gcd # That is used to avoid .0
# # Part 5: Compute rationals (from mage to rationals)
def plot2point(plot):
    """
    Input the image, recognise the image and extract the pixel data and extract the pixel coordinates 
    except for the pure white dots. to facilitate the path finding later.
    input:
    plot: The plot which is a sample picture.
    output:
    The location of non-white pixels in the plot.
    """
    image = Image.open(plot)
    width, height = image.size
    pixels = list(image.getdata())
    pixels = [pixels[i:i+width] for i in range(0, len(pixels), width)]
    non_white_pixels = []
    if len(pixels[0][0])==3:
        for y in range(height):
            for x in range(width):
                if pixels[y][x] != (255, 255, 255):
                    non_white_pixels.append((x, y))
    elif len(pixels[0][0])==1:
        for y in range(height):
            for x in range(width):
                if pixels[y][x] != (255):
                    non_white_pixels.append((x, y))   
    return non_white_pixels

def distance(point1, point2):
    '''
    find the distance between two points
    input:
    point1: the first point
    point2: the second point
    output:
    the distance suqare between point1 and point2
    '''
    x1, y1 = point1
    x2, y2 = point2
    return (x2-x1)**2 + (y2-y1)**2
def nearest_neighbor(points):
    '''
    rearrange the path with the shortest distance between two points.
    load the path to a new list.
    input:
    points: the list of points
    output:
    the path of the points as the order of the nearest neighbor
    '''
    path = [points[0]]
    unvisited = points[1:]
    path_number=[]
    while unvisited: # rearrange the point with the shortest distance
        last_point = path[-1]
        next_point = min(unvisited, key=lambda point: distance(last_point, point))
        path_num=shortest_path(last_point,next_point)
        path_number.append(path_num)
        path.append(next_point)
        unvisited.remove(next_point)
    return path , path_number

def shortest_path(start, end):
    """
    rearrange the path from start to end. 
    The sequence is 0,1,2,3 means the steps of the path.
    input:
    start: the start point
    end: the end point
    output:
    the shortest path from start to end
    """
    path = []
    current_location = start

    while current_location != end:
        x_diff = abs(end[0] - current_location[0])
        y_diff = abs(end[1] - current_location[1])

        if x_diff > y_diff:
            if current_location[0] < end[0]:
                current_location = (current_location[0] + 1, current_location[1])
                path.append("0")
            else:
                current_location = (current_location[0] - 1, current_location[1])
                path.append("2")
        else:
            if current_location[1] < end[1]:
                current_location = (current_location[0], current_location[1] + 1)
                path.append("3")
            else:
                current_location = (current_location[0], current_location[1] - 1)
                path.append("1")

    return ''.join(path)
def get_mn(location):
    '''
    get the number of M, N and the length of the sequence
    input:
    the location of the plot
    output:
    m,n of the plot
    '''
    path_point,path_re = nearest_neighbor(plot2point(location))
    path_re=''.join(path_re)

    m, n =from_prefix_period2mn('',path_re,4)
    
    return m,n, path_re


